import { Router } from '@angular/router';
import { ActivatedRoute } from '@angular/router';
import { MoviesService } from './../../core/services/movies/movies.component';
import { Component } from '@angular/core';

@Component({
  selector: 'app-schedule',
  templateUrl: './schedule.component.html',
  styleUrls: ['./schedule.component.scss']
})
export class ScheduleComponent {
movieData:any
time = '3:00pm'
adultsTickets = 1;
adultsTicketsPrice = 15.03;

childTickets = 0;
childTicketsPrice = 12.06;

seniorTickets = 0;
seniorTicketsPrice = 11.53;


totalTickets = this.adultsTickets + this.childTickets + this.seniorTickets


  constructor(
    private service: MoviesService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.route.paramMap.subscribe((params) => {
      this.movieData = service.getMovieData(params.get("title"))[0];
      console.log(this.movieData);


    });
  }



  chooseTime(time:string){
    this.time = time
console.log(this.time)

  }


  clickedSeat(slug:any,index:any){
    this.service.reserveSeat(slug, index, this.totalTickets)
    console.log('clicked Seat')
    }
}
