import { MoviesService } from './../../core/services/movies/movies.component';
import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent {

  moviesData;

  constructor(service:MoviesService){
    this.moviesData = service.getAllMovies();
  }

}
<div id="movies-list">
  <div class="row">

    <div class="col-md-3" *ngFor = "let movie of moviesData">
        <div class="movie-box">
              <img  src= movie.imageUrl/>
              <div class = 'av available'>
                <div class="status">
                  Tickets Available
                </div>
              </div>
        </div>
    </div>




  </div>
</div>
