import { MoviesService } from "./../../core/services/movies/movies.component";
import { Component } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";

@Component({
  selector: "app-movie",
  templateUrl: "./movie.component.html",
  styleUrls: ["./movie.component.scss"],
})
export class MovieComponent {
  movieData: any;
  // router: any;
  constructor(
    service: MoviesService,
    private route: ActivatedRoute,
    private router: Router
  ) {
    this.route.paramMap.subscribe((params) => {
      this.movieData = service.getMovieData(params.get("title"))[0];
      console.log(this.movieData);
    });
  }

  clickedTickets(slug: string) {
    console.log("Clicked Tickets");
    this.router.navigate(["movie", slug, "schedule"]);
  }
}
<div id="movie-page">
  <div
    class="movie-bg"
    [ngStyle]="{
      background: 'url(' + movieData.imageUrl + ')'
    }"
  ></div>

  <div class="container">
    <div class="movie-content">
      <div class="row">
        <div class="col-md-3">
          <img src="{{ movieData.imageUrl }}" class="movie-poster" />
        </div>
        <div class="col-md-6">
          <h1>{{ movieData.title }}</h1>
          <div class="release-date">
            Release Date: {{ movieData.releaseData }}
          </div>
          <p>
            {{ movieData.movieDescription }}
          </p>
        </div>
        <div class="col-md-3">
          <div class="btn-primary btn" (click) = "clickedTickets(slug)">Get Tickets</div>
        </div>
      </div>
    </div>
  </div>
</div>
